import React from 'react'
import {useState, useEffect} from 'react'
import Grid from '@material-ui/core/Grid'
import Paper from '@material-ui/core/Paper';
import Avatar from '@material-ui/core/Avatar';
import { makeStyles } from '@material-ui/core/styles';
import { Typography } from '@material-ui/core';
import { blue } from '@material-ui/core/colors';
import { useLocation } from "react-router-dom";


const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
    },
    paper: {
      padding: theme.spacing(1),
      textAlign: 'center',
      color: "black"
    },
    border:{
        borderLeft: "1px solid black"
    },
    bigBorder:{
        marginLeft: "10px",
        marginTop: "10px",
        border: "3px solid black"
    },
    borderScore:{
        borderLeft: "3px solid black",
        borderRight: "3px solid black",
        borderBottom: "3px solid black"
    },
    borderImage:{
        
    }
  }));

  
const Score = () => {

    const classes = useStyles();
    return (
        <div className={classes.root}>
        <Grid container xs={5} className={classes.bigBorder}>
          <Grid item xs={4}>
            <Typography className={classes.paper}></Typography>
          </Grid>
          <Grid item xs={4} className={classes.borderScore}>
            <Typography className={classes.paper}>40'</Typography>
          </Grid>
          <Grid item xs={4}>
            <Typography className={classes.paper}></Typography>
          </Grid>
          <Grid item container xs={2} justify="center">
            <Avatar alt="Remy Sharp" src="https://upload.wikimedia.org/wikipedia/fr/4/4f/RSC_Anderlecht_logo.png" />
          </Grid>
          <Grid item xs={4}>
            <Typography className={classes.paper}>1</Typography>
          </Grid>
          <Grid item xs={4} className={classes.border}>
            <Typography className={classes.paper}>2</Typography>
          </Grid>
          <Grid item container xs={2} justify="center">
            <Avatar  alt="Remy Sharp" src="https://upload.wikimedia.org/wikipedia/fr/4/4f/RSC_Anderlecht_logo.png" />
          </Grid>
        </Grid>
      </div>
  )
}

export default Score;
