import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import FormLabel from '@material-ui/core/FormLabel';
import FormControl from '@material-ui/core/FormControl';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormHelperText from '@material-ui/core/FormHelperText';
import Checkbox from '@material-ui/core/Checkbox';
import Button from '@material-ui/core/Button'

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  formControl: {
    margin: theme.spacing(3),
  },
}));

export default function Create() {
  const classes = useStyles();
  const [matches, setMatches] = Reaction.useState([]);
  const [live, setLive] = Reaction.useState([]);

  const handleChange = (event) => {
    setState({ ...state, [event.target.name]: event.target.checked });
  };
  function getLive(){
    fetch("https://api.sofascore.com/api/v1/sport/football/events/live")
        .then(response=> {
            return response.json();
        })
        .then(users => {
            setLive(users.events)
            /*for(let i = 0; i< users.events.length;i++){
                setHomeTeamName(users.events[i].homeTeam.name)
                setAwayTeamName(users.events[i].awayTeam.name)
                setHomeTeamScore(users.events[i].homeScore.current)
                setAwayTeamScore(users.events[i].homeScore.current)
            }*/
        })
  }

  const { gilad, jason, antoine } = state;
  const error = [gilad, jason, antoine].filter((v) => v).length !== 2;

  return (
    <div className={classes.root}>
      <FormControl required error={error} component="fieldset" className={classes.formControl}>
        <FormLabel component="legend">Choisis les matches que tu souhaites</FormLabel>
        <FormGroup>
          <FormControlLabel
            control={<Checkbox checked={gilad} onChange={handleChange} name="gilad" />}
            label="Gilad Gray"
          />
          <FormControlLabel
            control={<Checkbox checked={jason} onChange={handleChange} name="jason" />}
            label="Jason Killian"
          />
          <FormControlLabel
            control={<Checkbox checked={antoine} onChange={handleChange} name="antoine" />}
            label="Antoine Llorca"
          />
        </FormGroup>
        <FormHelperText>You can display an error</FormHelperText>
      </FormControl>
      <Button>Valider</Button>
    </div>
  );
}
