import React from 'react'
import Grid from '@material-ui/core/Grid'
import Paper from '@material-ui/core/Paper';
import Avatar from '@material-ui/core/Avatar';
import { makeStyles } from '@material-ui/core/styles';
import { Typography } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
    },
    avatar: {
      height: 75,
      width: 75,
    },

    control: {
      padding: theme.spacing(2),
    },
  }));
  const classes = useStyles();
  const box = [];

  // I initiated days as 0 instead of 31 and if days are less than 31 then
  // for loop iterates over it until it reaches that number.
  for (let days = 0; days < 31; days++) {
    // Then the code pushes each time it loops to the empty array I initiated.
    box.push(
        <Grid container spacing={3} xs={3}>
        <Grid item xs={5} justify="center"
        align="center">
            <Avatar src="https://upload.wikimedia.org/wikipedia/fr/4/4f/RSC_Anderlecht_logo.png" className={classes.avatar}/>
        </Grid>
        <Grid align="center" item xs={6}>
            <Typography variant="h2">0</Typography>
        </Grid>
        <Grid align="center" item xs={5}>
            <Avatar src="https://upload.wikimedia.org/wikipedia/fr/4/4f/RSC_Anderlecht_logo.png" className={classes.avatar}/>
        </Grid>
        <Grid align="center" item xs={6}>
            <Typography variant="h2">0</Typography>
        </Grid>
        <Grid align="center" item xs={12}>
            <Typography>90'</Typography>
        </Grid>
        <Grid style={{height: "50px"}} xs={12}>

        </Grid>
    </Grid>
    );
  }
export default function Notes() {
    
  return (
        {box}
        
      
  )
}
