import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import FormLabel from '@material-ui/core/FormLabel';
import FormControl from '@material-ui/core/FormControl';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormHelperText from '@material-ui/core/FormHelperText';
import Checkbox from '@material-ui/core/Checkbox';
import Button from '@material-ui/core/Button'

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  formControl: {
    margin: theme.spacing(3),
  },
}));

export default function Create() {
  const classes = useStyles();
  const [matches, setMatches] = React.useState([]);
  const [live, setLive] = React.useState([]);
  const items = []
  const [state, setState] = React.useState({
    gilad: true,
    jason: false,
    antoine: false,
  });
  const handleChange = (event) => {
    setState({ ...state, [event.target.name]: event.target.checked });
  };
  function getLive(){
    fetch("https://api.sofascore.com/api/v1/sport/football/events/live")
        .then(response=> {
            return response.json();
        })
        .then(users => {
            setLive(users.events)
            /*for(let i = 0; i< users.events.length;i++){
                setHomeTeamName(users.events[i].homeTeam.name)
                setAwayTeamName(users.events[i].awayTeam.name)
                setHomeTeamScore(users.events[i].homeScore.current)
                setAwayTeamScore(users.events[i].homeScore.current)
            }*/
        })
  }
  function onClickButton(){
    console.log(items)
  }
  function getMatches(){
    fetch("https://api.sofascore.com/api/v1/sport/football/scheduled-events/2022-03-25/inverse")
        .then(response=> {
            return response.json();
        })
        .then(users => {
            setMatches(users.events)
            /*for(let i = 0; i< users.events.length;i++){
                setHomeTeamName(users.events[i].homeTeam.name)
                setAwayTeamName(users.events[i].awayTeam.name)
                setHomeTeamScore(users.events[i].homeScore.current)
                setAwayTeamScore(users.events[i].homeScore.current)
            }*/
        })
  }
  getLive()
  getMatches()

  for (const [index, value] of live.entries()) {
    items.push(
      <FormControlLabel
      control={<Checkbox unchecked={value.homeTeam.name+" - "+value.awayTeam.name} onChange={handleChange} name={value.homeTeam.name+" - "+value.awayTeam.name} />}
      label={value.homeTeam.name+" - "+value.awayTeam.name}
    />
        )
}
for (const [index, value] of matches.entries()) {
  if(value.status.description!="Ended"){
    items.push(
      <FormControlLabel
      control={<Checkbox unchecked={value.homeTeam.name+" - "+value.awayTeam.name} onChange={handleChange} name={value.homeTeam.name+" - "+value.awayTeam.name} />}
      label={value.homeTeam.name+" - "+value.awayTeam.name}
    />)
  }
  
      
}

  const { gilad, jason, antoine } = state;
  const error = [gilad, jason, antoine].filter((v) => v).length !== 2;

  return (
    <div>
    <div className={classes.root}>
      <FormControl required error={error} component="fieldset" className={classes.formControl}>
        <FormLabel component="legend">Choisis les matches que tu souhaites</FormLabel>
        <FormGroup>
          {items}
          
        </FormGroup>
        <FormHelperText>You can display an error</FormHelperText>
      </FormControl>
      
    </div>
    <div>
        <Button onClick={()=>onClickButton()}>Valider</Button>
    </div>
    </div>
    
  );
}
