import React from 'react'
import {useState, useEffect} from 'react'
import Grid from '@material-ui/core/Grid'
import Paper from '@material-ui/core/Paper';
import Avatar from '@material-ui/core/Avatar';
import { makeStyles } from '@material-ui/core/styles';
import { Typography } from '@material-ui/core';
import { blue } from '@material-ui/core/colors';
import { useLocation } from "react-router-dom";


const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
    },
    score:{
    padding: theme.spacing(1),
      textAlign: 'center',
      color: "red",
      fontWeight: "bold",
      fontFamily: "Lucida Console",
      fontSize: "20px"

    },
    paper: {
      padding: theme.spacing(1),
      textAlign: 'center',
      color: "white",
      fontSize: "10px",
      fontFamily: "Lucida Console",
      backgroundColor: "#B0B0B0"
    },
    teamName: {
        padding: theme.spacing(1),
        paddingLeft: "30px",
        color: "white",
        fontSize: "20px",
        fontFamily: "Lucida Console"
      },
    borderCity:{
        borderLeft: "3px solid black",
        borderTop: "3px solid black",
        backgroundColor: "#3ACAF0"
    },
    borderAway:{
        borderLeft: "3px solid black",
        backgroundColor: "#B145D9"
    },
    borderS:{
        borderRight: "3px solid black",
        backgroundColor: "#B0B0B0",
        fontFamily: "Lucida Console",
        paddingLeft: "15px"
    },
    bigBorder:{
        marginLeft: "10px",
        marginTop: "10px",
        borderBottom: "3px solid black",
        
    },
    borderScore:{
        borderLeft: "3px solid black",
        borderTop: "3px solid black",
        borderRight: "3px solid black",
        backgroundColor: "#B0B0B0"
    },
    borderSTop:{
        borderRight: "3px solid black",
        borderTop: "3px solid black",
        backgroundColor: "#B0B0B0",
        fontFamily: "Lucida Console",
        paddingLeft: "15px"

    }
  }));

  
const Score = () => {

    const classes = useStyles();
    return (
        <div className={classes.root}>
        <Grid container xs={2} className={classes.bigBorder}>
          <Grid item xs={4}>
            <Typography></Typography>
          </Grid>
          <Grid item xs={4} className={classes.borderScore}>
            <Typography className={classes.score}>40'</Typography>
          </Grid>
          <Grid item xs={4}>
            <Typography></Typography>
          </Grid>
          <Grid item container xs={8} className={classes.borderCity}>
            <Typography className={classes.teamName}>MCI</Typography>
          </Grid>
          <Grid item container xs={4} className={classes.borderSTop}>
            <Typography className={classes.paper}>1</Typography>
          </Grid>
          <Grid item container xs={8} className={classes.borderAway}>
            <Typography className={classes.teamName}>VER</Typography>
          </Grid>
          <Grid item container xs={4} className={classes.borderS}>
            <Typography className={classes.paper}>2</Typography>
          </Grid>
        </Grid>
      </div>
  )
}

export default Score;
