import React from 'react'
import {useState, useEffect} from 'react'
import Grid from '@material-ui/core/Grid'
import Paper from '@material-ui/core/Paper';
import Avatar from '@material-ui/core/Avatar';
import { makeStyles } from '@material-ui/core/styles';
import { Typography } from '@material-ui/core';
import { blue } from '@material-ui/core/colors';
import { useLocation } from "react-router-dom";


const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
    },
    score:{
    padding: theme.spacing(1),
      textAlign: 'center',
      color: "red",
      fontWeight: "bold",
      fontFamily: "Lucida Console",
      fontSize: "10px"

    },
    paper: {
      padding: theme.spacing(1),
      textAlign: 'center',
      color: "white",
      paddingLeft: "10px",
      fontSize: "20px",
      fontFamily: "Lucida Console",
      backgroundColor: "#B0B0B0"
    },
    teamName: {
        padding: theme.spacing(1),
        paddingLeft: "15px",
        color: "white",
        fontSize: "20px",
        fontFamily: "Lucida Console"
      },
    borderCity:{
        borderTop: "3px solid black",
        backgroundColor: "#3ACAF0"
    },
    borderAway:{
        backgroundColor: "#B145D9"
    },
    borderS:{
        borderRight: "3px solid black",
        backgroundColor: "#B0B0B0",
        fontFamily: "Lucida Console",
    },
    bigBorder:{
        marginLeft: "10px",
        marginTop: "10px",
        borderBottom: "3px solid black",
        
    },
    borderScore:{
        borderLeft: "3px solid white",
        borderTop: "3px solid white",
        borderRight: "3px solid white",
        borderBottom: "3px solid white"
    },
    borderSTop:{
        borderRight: "3px solid black",
        borderTop: "3px solid black",
        backgroundColor: "#B0B0B0",
        fontFamily: "Lucida Console",

    }
  }));

  
const Score = () => {

    const classes = useStyles();
    return (
        <div className={classes.root}>
        <Grid container xs={2} className={classes.bigBorder}>
          <Grid item xs={4}>
            <Typography></Typography>
          </Grid>
          <Grid item xs={4}>
            <Typography className={classes.score}></Typography>
          </Grid>
          <Grid item xs={4}>
            <Typography></Typography>
          </Grid>
          <Grid item container xs={5} className={classes.borderCity}>
            <Avatar src="https://upload.wikimedia.org/wikipedia/fr/thumb/b/ba/Logo_Manchester_City_2016.svg/1200px-Logo_Manchester_City_2016.svg.png"></Avatar>
          </Grid>
          <Grid item container xs={5} className={classes.borderCity}>
            <Typography className={classes.teamName}>MCI</Typography>
          </Grid>
          <Grid item container xs={2} className={classes.borderSTop}>
            <Typography className={classes.paper}>1</Typography>
          </Grid>
          <Grid item container xs={5} className={classes.borderAway} >
            <Avatar src="https://upload.wikimedia.org/wikipedia/fr/thumb/b/ba/Logo_Manchester_City_2016.svg/1200px-Logo_Manchester_City_2016.svg.png"></Avatar>
          </Grid>
          <Grid item container xs={5} className={classes.borderAway} >
            <Typography className={classes.teamName}>LIV</Typography>
          </Grid>
          <Grid item container xs={2} className={classes.borderS}>
            <Typography className={classes.paper}>2</Typography>
          </Grid>
        </Grid>
      </div>
  )
}

export default Score;
