import React from 'react'
import {useState} from 'react'
import Grid from '@material-ui/core/Grid'
import Paper from '@material-ui/core/Paper';
import Avatar from '@material-ui/core/Avatar';
import { makeStyles } from '@material-ui/core/styles';
import { Typography } from '@material-ui/core';
import { blue } from '@material-ui/core/colors';

const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
    },
    avatar: {
      height: 75,
      width: 75,
    },

    control: {
      padding: theme.spacing(2),
    }
  }));

export default function Notes() {
    const classes = useStyles();
    const elements = ['blue', 'red', 'yellow'];
    const away=['yellow','blue','red']
    const items = []
    const items2 = []
    const [homeTeamName, setHomeTeamName] = useState([]);
    const [awayTeamName, setAwayTeamName] = useState([]);
    const [homeTeamScore, setHomeTeamScore] = useState([]);
    const [awayTeamScore, setAwayTeamScore] = useState([]);
    const [score, setScore] = useState([]);
    function getAllData(){
        fetch("https://api.sofascore.com/api/v1/sport/football/events/live")
        .then(response=> {
            return response.json();
        })
        .then(users => {
            setScore(users.events)
            /*for(let i = 0; i< users.events.length;i++){
                setHomeTeamName(users.events[i].homeTeam.name)
                setAwayTeamName(users.events[i].awayTeam.name)
                setHomeTeamScore(users.events[i].homeScore.current)
                setAwayTeamScore(users.events[i].homeScore.current)
            }*/
        })
    }
    //getAllData()
    var t=setInterval(getAllData,5000);
    //console.log(score)
    console.log(score)
    for (const [index, value] of away.entries()) {
        items.push(
            <Grid container spacing={2}>
                <Grid item xs={5} justify="center" align="center" style={{backgroundColor:value}}>
                    <Avatar src="https://upload.wikimedia.org/wikipedia/fr/4/4f/RSC_Anderlecht_logo.png" className={classes.avatar}/>
                </Grid>
                <Grid align="center" item xs={6} style={{backgroundColor:value}}>
                    <Typography variant="h2">{homeTeamScore[index]}</Typography>
                </Grid>
                <Grid item xs={5} justify="center" align="center" style={{backgroundColor:away[index]}}>
                    <Avatar src="https://upload.wikimedia.org/wikipedia/fr/4/4f/RSC_Anderlecht_logo.png" className={classes.avatar}/>
                </Grid>
                <Grid align="center" item xs={6} style={{backgroundColor:away[index]}}>
                    <Typography variant="h2">{awayTeamScore[index]}</Typography>
                </Grid>
                <Grid align="center" item xs={12}>
                    <Typography>{value}</Typography>
                </Grid>
                <Grid style={{height: "50px"}} xs={12}>

                </Grid>
            </Grid>
            )
    }
  return (
        <Grid container spacing={4} xs={3} style={{marginTop: "20px"}}>
            {items}
        </Grid>
        
      
  )
}
