import React from 'react'
import {useState, useEffect} from 'react'
import Grid from '@material-ui/core/Grid'
import Paper from '@material-ui/core/Paper';
import Avatar from '@material-ui/core/Avatar';
import { makeStyles } from '@material-ui/core/styles';
import { Typography } from '@material-ui/core';
import { blue } from '@material-ui/core/colors';
import { useLocation } from "react-router-dom";
import { useParams } from "react-router-dom";


  
const Data = () => {
    const { id } = useParams()
    const MINUTE_MS = 30000;
    const [nameHomeTeam, setHomeTeam] = useState();
    const [nameAwayTeam, setAwayTeam] = useState();
    const [scoreHome, setScoreHome] = useState();
    const [scoreAway, setScoreAway] = useState();
    const [logoHomeTeam, setLogoHomeTeam] = useState();
    const [logoAwayTeam, setLogoAwayTeam] = useState();
    const getData = () => {
        fetch("https://api.sofascore.com/api/v1/event/"+id)
        .then(response=> {
             return response.json();
        })
        .then(users => {
            setScoreHome(users.event.homeScore.current);
            setScoreAway(users.event.awayScore.current);
            setHomeTeam(users.event.homeTeam.name);
            setAwayTeam(users.event.awayTeam.name);
        })
    }
    const getLogoHomeTeam = () =>{
        const url="https://api.sofascore.app/api/v1/team/"+id+"/image"
        fetch(url)
        .then(response=> {
             return response.blob();
        })
        .then(users => {
            const imageObjectURL = URL.createObjectURL(users);
            setLogoHomeTeam(imageObjectURL);
            
        })
    }
    const getLogoAwayTeam = () =>{
        const url="https://api.sofascore.app/api/v1/team/"+id+"/image"
        fetch(url)
        .then(response=> {
             return response.blob();
        })
        .then(users => {
            const imageObjectURL = URL.createObjectURL(users);
            setLogoAwayTeam(imageObjectURL);
        })
    }
    useEffect(() => {
        getData()
        const interval = setInterval(() => {
            getData()
            getLogoHomeTeam()
            getLogoAwayTeam
        }, MINUTE_MS);
        return () => clearInterval(interval);
    },[])
    return (
        <div>
            <h1>{scoreHome}</h1>
            <h1>{scoreAway}</h1>
            <h1>{nameHomeTeam}</h1>
            <h1>{nameAwayTeam}</h1>
            <Avatar src={logoAwayTeam}/>
            <Avatar src={logoHomeTeam}/>
        </div>
        
    )
}

export default Data;
